import React from 'react';
import { Text, KeyboardAvoidingView, View, FlatList, StyleSheet, TextInput, TouchableOpacity} from 'react-native';
import db from '../config'

export default class LoginScreen extends React.Component {
    constructor(){
        super()
        this.state= {
            emailId : '',
            password:''
        }
    }
    render(){
        return (
            <KeyboardAvoidingView>
                <View >
                    <TextInput 
                     placeholder="Email"
                     keyboardType='email-address'
                     style={styles.loginBox}
                     onChangeText={(text)=>{
                        this.setState({
                            emailId:text
                        })
                     }}
                    />
                    <TextInput 
                     placeholder="Password"
                     keyboardType='password'
                     style={styles.loginBox}
                     secureTextEntry={true}
                     onChangeText={(text)=>{
                        this.setState({
                            password:text
                        })
                     }}
                    />
                    <TouchableOpacity style={styles.to}>
                        <Text style={styles.text}>
                            Submit
                        </Text>
                    </TouchableOpacity>
                </View>
            </KeyboardAvoidingView>
        )
    }
}

const styles = StyleSheet.create({
    loginBox:{
        width: '300',
        height:40,
        borderWidth:1.5,
        fontSize:20,
        margin: '10',
        paddingLeft:10,
    },

    to:{
        backgroundColor:'red',
        width:80,
        alignSelf:"center",
        alignContent:"center",
        marginTop:20,
        borderRadius:7,
        border: solid
    },
    text:{
        color:"yellow"
    }



})
