import React from 'react';
import { Text, View , FlatList ,AppRegistry , Alert } from 'react-native';
import db from '../config';
import firebase from 'firebase';
//import { SearchBar } from 'react-native-elements'
export default class Searchscreen extends React.Component {
  constructor(){
    super()
    this.state = {
      allTransactions : []
    }
  }

  componentDidMount(){
    var allTransactions = [];
    var trans = db.collection('transaction').get().then((querySnapshot)=>{
      querySnapshot.forEach((doc)=>{
        allTransactions.push(doc.data())
      })
    })
    console.log(allTransactions)
  }

  renderSeparator = () => {  
    return (  
        <View  
            style={{  
                height: 1,  
                width: "100%",  
                backgroundColor: "#000",  
            }}  
        />  
    );  
};  
//handling onPress action  
getListViewItem = (item) => {  
    Alert.alert(item.key);  
}  


    render() {
      return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <FlatList  
                    data={[  
                        {key: 'Android'},{key: 'iOS'}, {key: 'Java'},{key: 'Swift'},  
                        {key: 'Php'},{key: 'Hadoop'},{key: 'Sap'},  
                        {key: 'Python'},{key: 'Ajax'}, {key: 'C++'},  
                        {key: 'Ruby'},{key: 'Rails'},{key: '.Net'},  
                        {key: 'Perl'},{key: 'Sap'},{key: 'Python'},  
                        {key: 'Ajax'}, {key: 'C++'},{key: 'Ruby'},  
                        {key: 'Rails'},{key: '.Net'},{key: 'Perl'}  
                    ]}  
                    renderItem={({item}) =>  
                        <Text
                              onPress={this.getListViewItem.bind(this, item)}>{item.key}</Text>}  
                    ItemSeparatorComponent={this.renderSeparator}  
                />  
        </View>
      );
    }
  }

  AppRegistry.registerComponent('AwesomeProject', () => FlatListBasics);
