import React from 'react';
import { Text, View , FlatList ,AppRegistry , Alert , StyleSheet } from 'react-native';
import db from '../config';
import firebase from 'firebase';
//import { SearchBar } from 'react-native-elements'
export default class Searchscreen extends React.Component {
  constructor(){
    super()
    this.state = {
      allTransactions : []
    }
  }

  componentDidMount(){
    var allTransactions = [];
    var trans = db.collection('transaction').get().then((querySnapshot)=>{
      querySnapshot.forEach((doc)=>{
        allTransactions.push(doc.data())
      })
    })
    console.log(allTransactions)
    this.setState({allTransactions:allTransactions})
  }

  renderSeparator = () => {  
    return (  
        <View  
            style={{  
                height: 1,  
                width: "100%",  
                backgroundColor: "#000",  
            }}  
        />  
    );  
};  
//handling onPress action  
getListViewItem = (item) => {  
    Alert.alert(item.key);  
}  


    render() {
      var data = this.state.allTransactions
      return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <FlatList  
                    renderItem={({item}) =>  
                        <Text style={styles.item}  >{data}</Text>}  />  
        </View>
      );
    }
  }
  const styles = StyleSheet.create({  
    container: {  
        flex: 1,  
    },  
    item: {  
        padding: 10,  
        fontSize: 18,  
        height: 44,  
    },  
})  
  AppRegistry.registerComponent('AwesomeProject', () => FlatListBasics);
